import { Button, Card, CardBody, CardFooter, CardHeader, CardImg, Col, Row } from "react-bootstrap";
import Imgicon from '/project/firstapp/src/Images/Assest/NullIcon.png';

function NewsCard({newsitem}){
    let lenght = 10;
    return(
            <>
                <Row>
                        {
                            newsitem.map((item , ) => (
                                <Col xs={12} md={3} lg={3}>
                                    <Card className="m-2" style={{border: "2px solid green"}}>
                                        <CardImg src={item.urlToImage != null ? item.urlToImage : Imgicon}/>
                                        <CardHeader className="bg-secondery" style={{color:"black"}}>
                                            <h5>{item.title > 8 ? item.title.substring(0, 10): item.title}</h5>
                                        </CardHeader>
                                        <CardBody>
                                           <p>{item.description > 20 ? item.description.substring(0, 20): item.description}</p>
                                        </CardBody>
                                        <CardFooter>
                                            <caption>
                                                {item.author} <br/>
                                                {item.publishedAt}
                                            </caption>
                                            <Button variant="primary" href={item.url} target="_blank">View More</Button>
                                        </CardFooter>
                                    </Card>
                                </Col>
                            ))
                        }
                </Row>
            </>
        );
}

export default NewsCard;
