import { Button, Card, CardBody, CardFooter, CardHeader, CardImg, Col, Row } from "react-bootstrap";
import Imgicon from '/project/firstapp/src/Images/Assest/NullIcon.png';
function NewsCard({newsitem}){
//    const [details , setdetails] = useState([]);
//  // const [content, publishedAt, urlToImage, url, description, title, author, source] = newsitem;
//  useEffect(()=>{
     
//   }, []);
    return(
            <>
                <Row>
                        {
                            newsitem.map((item) => (
                                <Col xs={3}>
                                    <Card className="m-2" style={{border: "2px solid green"}}>
                                        <CardHeader>
                                            <h5>{item.title}</h5>
                                        </CardHeader>
                                        <CardImg src={item.urlToImage != null ? item.urlToImage : Imgicon}/>
                                        <CardBody>
                                            <p>{(item.description).substr(0, 150)}</p>
                                        </CardBody>
                                        <CardFooter>
                                            <caption>
                                                {item.author} <br/>
                                                {item.publishedAt}
                                            </caption>
                                            <Button variant="primary" href={item.url}>View More</Button>
                                        </CardFooter>
                                    </Card>
                                </Col>
                            ))
                        }
                </Row>
            </>
        );
}

export default NewsCard;
