import { Button, Card, CardBody, CardFooter, CardHeader, CardImg } from "react-bootstrap";

function NewsCard({newsitem}){
//    const [details , setdetails] = useState([]);
//  // const [content, publishedAt, urlToImage, url, description, title, author, source] = newsitem;
//  useEffect(()=>{
     
//   }, []);
    return(
            <>
            <Grid></Grid>
                {
                    newsitem.map((item) => (
                        <Card>
                            <CardHeader>
                                <h5>{item.title}</h5>
                            </CardHeader>
                            <CardImg src={item.urlToImage}/>
                            <CardBody>
                                <p>{item.description}</p>
                            </CardBody>
                            <CardFooter>
                                <caption>
                                    {item.author} <br/>
                                    {item.publishedAt}
                                </caption>
                                <Button variant="primery" href={item.url}>View More</Button>
                            </CardFooter>
                        </Card>
                    ))
                }
            </>
        );
}

export default NewsCard;
