import { useState } from "react";
import { Button, Card, CardBody, CardFooter, CardHeader, CardImg } from "react-bootstrap";

function NewsCard({newsitem}){

  const [source, author, title, description, url, urlToImage, publishedAt, content] = newsitem;

    return(
       <>
           <Card>
            <CardHeader>
                <h5 style={{fontFamily:"inherit"}}>{title}</h5>
            </CardHeader>
            <CardImg variant="top" src={urlToImage}> </CardImg>
            <CardBody>
                <p>
                    {description}
                </p>
            </CardBody>
            <CardFooter>
                <p>
                    {author}<br/>
                    {publishedAt}
                </p>
                <Button variant="primary" href={url}></Button>
            </CardFooter>
          </Card>
       </>
    );
}

export default NewsCard;
